import React, {Component} from 'react';
import './App.css';
import Header from './components/Header';
import Nav from './components/Nav';
import Article from './components/Article';
import Controls from './components/Controls';
import Write from './components/controls/Write';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      index: [],
      idxNxtVal: 3,
      cntState: 0,
      header:{title:'react-app', body:'react practicing and discovering'},
      articles : [
        {id:1, title:'menu1', body:'article body'},
        {id:2, title:'menu2', body:'article2 body'},
        {id:3, title:'menu3', body:'article3 body'}
      ]
    }
  }
  render(){
    let cntState = this.state.cntState,
        control = null;

    switch(cntState){
      case 1 :
        console.log("mode is 1");
        control = <Write
          onSubmit={function(_title, _content){
            if(_title === "" || _title === null){
              alert("제목을 입력해주세요.");
            }else if(_content === "" || _content === null){
              alert("내용을 입력해주세요.");
            }else{
              let _idNxtVal = this.state.idxNxtVal + 1
              this.state.articles.push(
                {id : _idNxtVal, title : _title, body : _content}
              )
              this.setState({
                idxNxtVal :_idNxtVal,
                cntState : 0,
                articles : this.state.articles
              });
              alert(_idNxtVal + "번글 등록완료.");
            }
          }.bind(this)}></Write>
        break;
      case 2 :
        console.log("mode is 2");
        // <Modify></Modify>
        break;
      case 3 :
        console.log("mode is 3");
        // <Delete></Delete>
        break;
      default :
        console.log("mode is 0");
    }
    return (
      <div className="App">
        <Header
          title={this.state.header.title}
          body={this.state.header.body}
        ></Header>

        <Controls
          getControls={
            function(cntNum){
              this.setState({cntState : cntNum})
            }.bind(this)
          }></Controls>
        {control}

        <Nav
          index={this.state.index}
          data={this.state.articles}
          changePage={
            function(arr){
              this.setState({index : arr})
            }.bind(this)}
        ></Nav>

        <Article index={this.state.index} data={this.state.articles}></Article>
      </div>
    );
  }
}

export default App;
