import React, {Component, Fragment} from 'react';
import styled from 'styled-components'
import './App.css';
import Header from './components/Header';
import Nav from './components/Nav';
import Article from './components/Article';
import Controls from './components/Controls';
import Write from './components/controls/Write';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      index: [],
      idxNxtVal: 3,
      cntState: 0,
      header:{title:'react-app', body:'react practicing and discovering'},
      articles : [
        {id:1, title:'menu1', body:'article body'},
        {id:2, title:'menu2', body:'article2 body'},
        {id:3, title:'menu3', body:'article3 body'}
      ]
    }
  }
  render(){
    let cntState = this.state.cntState,
        control = null;

    switch(cntState){
      case 1 :
        control = <Write
          onSubmit={function(_title, _content){
              let _idNxtVal = this.state.idxNxtVal + 1
              this.state.articles.concat(
                {id : _idNxtVal, title : _title, body : _content}
              )
              this.setState({
                idxNxtVal :_idNxtVal,
                cntState : 0,
                articles : this.state.articles
              });
              alert("No." + _idNxtVal + " article is ready.");
          }.bind(this)}></Write>
        break;
      case 2 :
        console.log("mode is 2");
        // <Modify></Modify>
        break;
      case 3 :
        console.log("mode is 3");
        // <Delete></Delete>
        break;
      default :
        console.log("mode is 0");
    }
    return (
      <Fragment>
        <Header
          title={this.state.header.title}
          body={this.state.header.body}
        ></Header>

        <Controls
          getControls={
            function(cntNum){
              this.setState({cntState : cntNum})
            }.bind(this)
          }></Controls>

        <Navigation>
          this is styled-components
        </Navigation>

        <Nav
          index={this.state.index}
          data={this.state.articles}
          changePage={
            function(arr){
              this.setState({index : arr})
            }.bind(this)}
        ></Nav>

        <Article index={this.state.index} data={this.state.articles}></Article>
      </Fragment>
    );
  }
}

//styled-components
const Navigation = styled.div`
  max-width: 1200px;
  border: 1px solid #000;
  font-size: 100px;
  text-align: center;
`;
export default App;
