import React, {Component, Fragment} from 'react';
import styled, {createGlobalStyle} from 'styled-components';
import Header from './components/Header';
import Nav from './components/Nav';
import Article from './components/Article';
import Controls from './components/Controls';
import Write from './components/controls/Write';
import Modify from './components/controls/Modify';

const GlobalStyle = createGlobalStyle`
  body,h1,button,ul,li,a,p{
    margin: 0;
    padding: 0;
    list-style: none
  }
  body{
    background: #eeeeee;
  }
`
const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 1em 0em;
  background: #fff;
`

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      index: [],
      idxNxtVal: 3,
      cntState: 0,
      header:{title:'react-app', body:'create-react-app + styled-components'},
      articles : [
        {id:1, title:'menu1', body:'article body'},
        {id:2, title:'menu2', body:'article2 body'},
        {id:3, title:'menu3', body:'article3 body'}
      ]
    }
  }

  editorWrite(cntState){
    let editor = <Write
      onSubmit={function(_title, _content){
          let _idNxtVal = this.state.idxNxtVal + 1
          let articleAdded = this.state.articles.concat(
            {id : _idNxtVal, title : _title, body : _content}
          )
          this.setState({
            idxNxtVal :_idNxtVal,
            cntState : 0,
            articles : articleAdded
          });
          alert("No." + _idNxtVal + " article is ready.");
      }.bind(this)}></Write>
    return editor;
  };

  editorFunc(cntState){
        switch(cntState){
          case 1 :
            return this.editorWrite(cntState);
          case 2 :
            console.log("mode is 2");
            // <Modify></Modify>
            break;
          case 3 :
            console.log("mode is 3");
            // <Delete></Delete>
            break;
          default :
            console.log("mode is 0");
        }

  }


  render(){
    console.log("AppRender");
    return (
      <Fragment>
        <GlobalStyle/>

        <Container>
          <Header
            title={this.state.header.title}
            body={this.state.header.body}
          ></Header>
        </Container>


        <Controls
          getControls={
            function(cntNum){
              this.setState({cntState : cntNum})
            }.bind(this)
          }></Controls>

        {this.editorFunc(this.state.cntState)}

        <Nav
          index={this.state.index}
          data={this.state.articles}
          changePage={
            function(arr){
              this.setState({index : arr})
            }.bind(this)}
        ></Nav>

        <Article index={this.state.index} data={this.state.articles}></Article>
      </Fragment>
    );
  }
}

export default App;
