import React, {Component} from 'react';

class Nav extends Component {
  render(){
    let menus = [];
    let index = this.props.index;
    let datas = this.props.data;

    function idChk(id){
      var find = index.find(function(item){
        console.log("item: " + typeOf(item));
        console.log("id: " + typeOf(id));
        console.log("item === id : " + id === item);
        console.log("index: " + index);
        return item === id
      })
    }

    datas.forEach(data =>
      menus.push(
        <li key={data.id}>
          <a
            href={"/content/" + data.id}
            onClick={function(e){
              e.preventDefault();
              if(data.id !== idChk(data.id)){
                console.log("idChk:" + idChk(data.id));
                index.push(data.id);
              }else{
                index.splice(index.indexOf(data.id),1);
                console.log(index);
              }
            }.bind(this)}
          >
            {data.title}
          </a>
        </li>)
    )

    return(
      <nav>
        <ul>
          {menus}
        </ul>
      </nav>
    )
  }
}

export default Nav;
