import React, {Component} from 'react';
import './App.css';
import Header from './components/Header';
import Nav from './components/Nav';
import Article from './components/Article';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      header:{title:'react-app', body:'react practicing and discovering'},
      menus:[
        {id:1, title:'menu1', body:'this is a menu no.1'},
        {id:2, title:'menu2', body:'this is a menu no.2'},
        {id:3, title:'menu3', body:'this is a menu no.3'},
      ],
      articles : [
        {title:'article title', body:'article body'}
      ]
    }
  }
  render(){
    return (
      <div className="App">
        <Header
          title={this.state.header.title}
          body={this.state.header.body}>
        </Header>
        <Nav data={this.state.menus}></Nav>
        <Article data={this.state.articles}></Article>
      </div>
    );
  }
}

export default App;
