import React, {Component} from 'react';
import './App.css';
import Header from './components/Header';
import Nav from './components/Nav';
import Article from './components/Article';
import Contorls from './components/Controls'

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      index: [],
      header:{title:'react-app', body:'react practicing and discovering'},
      articles : [
        {id:1, title:'menu1', body:'article body'},
        {id:2, title:'menu2', body:'article2 body'},
        {id:3, title:'menu3', body:'article3 body'}
      ]
    }
  }
  render(){
    return (
      <div className="App">
        <Header
          title={this.state.header.title}
          body={this.state.header.body}>
        </Header>
        <Contorols></Contorols>
        <Nav
          index={this.state.index}
          data={this.state.articles}
          changePage={
            function(arr){
              this.setState({index : arr})
            }.bind(this)}
        >
        </Nav>
        <Article index={this.state.index} data={this.state.articles}></Article>
      </div>
    );
  }
}

export default App;
