import React, {Component} from 'react';
import './App.css';
import Header from './components/Header';
import Nav from './components/Nav';
import Article from './components/Article';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      index: [3,2],
      header:{title:'react-app', body:'react practicing and discovering'},
      menus:[
        {id:1, title:'menu1', body:'this is a menu no.1'},
        {id:2, title:'menu2', body:'this is a menu no.2'},
        {id:3, title:'menu3', body:'this is a menu no.3'},
      ],
      articles : [
        {id:1, title:'article title', body:'article body'},
        {id:2, title:'article2 title', body:'article2 body'},
        {id:3, title:'article3 title', body:'article3 body'}
      ]
    }
  }
  render(){
    return (
      <div className="App">
        <Header
          title={this.state.header.title}
          body={this.state.header.body}>
        </Header>
        <Nav
          data={this.state.menus}
          getIdx={function(id){
            let idxArr = this.state.index;
            let artArr = this.state.articles;
            let idCheck = idxArr.find(function(item){
              return item === id
            })
            console.log(idCheck);
            if(idxArr.length < artArr.length){
              if(id === artArr[id-1])
              idxArr.push(id);
              console.log(idxArr);
            }else if(idxArr.length <= artArr.length){
              console.log("full")
            }
          }.bind(this)}
        >
        </Nav>
        <Article index={this.state.index} data={this.state.articles}></Article>
      </div>
    );
  }
}

export default App;
