import React, { Component } from 'react';

class Modify extends Component{
  state = {
    title: this.props.data.title,
  }
  formChanger = (e) => {
    this.setState({title: e.target.value})
  }


  render(){
    return(
      <div>
        modify
        <form action="/modify" method="post"
          onSubmit={function(e){
            e.preventDefault();
            let _id = this.props.modiID,
                _title = e.target.title.value,
                _content = e.target.content.value;

            if(_title === "" || _title === null){
              alert("change the title");
            }else if(_content === "" || _content === null){
              alert("change the content.");
            }else{
            this.props.onSubmit(_title, _content);}
          }.bind(this)}
          >
          <p>
          <input
            name="title"
            type="text"
            placeholder="title"
            value={this.state.title}
            onChange={this.formChanger}
            ></input>
          </p>

          <p>
          <textarea name="content" placeholder="content"></textarea>
          </p>

          <p>
          <input type="submit"></input>
          </p>
        </form>
      </div>
    )
  }
}

export default Modify;
